mapper.select:
SELECT *
FROM xupt_research_team_work_report_table
WHERE author = #{userAccount}
  AND type = #{type}

SELECT COUNT(*)
FROM xupt_research_team_work_report_table
WHERE id = #{id}
  AND author = #{userAccount}

mapper.delete:
DELETE
FROM xupt_research_team_work_report_table
WHERE id = #{id}
  AND author = #{userAccount}
